var fxs = require( '../index' )
	, path = require( 'path' );

exports['client-missing-server'] = function( test )
{
	test.expect( 1 );
	
	//attempt to start a client with no server running
	var client = fxs.ping( 8043 );
	client.on( "error", function( err )
	{
		console.log( "fxs-test: error %j", err );
		test.throws( function(){ throw err; } );
		test.done();
	} );
}