var fxs = require( '../index' )
	, path = require( 'path' );

exports['missing-cross-domain-policy'] = function( test )
{
	test.expect( 2 );
	var server = fxs.server();
	server.on( "error", function( err )
	{
		test.ok( !server.connected );
		console.log( "fxs-test: error %j", err );
		test.throws( function(){ throw err; } );
		test.done();
	} );
	server.load( path.join( __dirname, "missing-cross-domain-policy.json" ) );
}